import React, { useState, useEffect } from 'react';
import NavWrapper from '../components/Nav';
import PlayListCard from '../components/PlayListCard';
import styled from 'styled-components';
import PlayWrapCard from '../components/PlayWrapCard';

const tags = ['#sleep', '#morning', '#relax', '#music', '#anxiety', '#meditation', '#healing', '#relaxation', '#calm', '#stress'];

const PlayListPage = (props) => {

    const [number, setNumber] = useState(0);
    const [translate, setTranslate] = useState(0);
    const [button, setButton] = useState("hidden");
    const [staffPick, setStaffPick] = useState([]);

    useEffect(() => {
        fetch(`http://10.58.2.238:8005/content/playlistmain`)
            .then((response) => response.json())
            .then((response) => {
                setStaffPick(response.staff_pick);
            })
    }, [])
    // console.log(staffPick.length);

    const handleClickRight = () => {
        setNumber(number + 1)
        setTranslate(translate - 424.75)
        console.log("number: ", number);
        if (number >= 0) {
            setButton("visible");
        }
    }

    const handleClickLeft = () => {
        setNumber(number - 1)
        setTranslate(translate + 424.75)
        console.log("sas: ", number);
        if (number === 1) {
            setButton("hidden");
        }
        // if (number === staffPick.length - 4) {
        //     setButton("hidden");
        // }
    }

    const selectPlay = () => {
        props.history.push("/playlist")
    }

    return (
        <>
            <NavWrapper />
            <TitleArea>
                <TitleText>Playlists</TitleText>
                <TitleDescription>
                    Create public or private Playlists from your Library. <br />You can also browse and follow your friendâ€™s.
                    <BackgroundImg />
                </TitleDescription>
            </TitleArea>
            <TagListArea>
                {tags.map((tag) => {
                    return (
                        <TagList>{tag}</TagList>
                    );
                })}
            </TagListArea>
            <TagTitle>Staff Picks</TagTitle>
            <NextButton>
                <ButtonLabel onClick={handleClickRight}>
                    <Icon viewBox="0 0 24 24">
                        <path d="M 10 6 L 8.59 7.41 L 13.17 12 l -4.58 4.59 L 10 18 l 6 -6 Z" />
                    </Icon>
                </ButtonLabel>
            </NextButton>
            <PrevButton leftButton={button}>
                <ButtonLabel onClick={handleClickLeft}>
                    <Icon viewBox="0 0 24 24">
                        <path d="M 10 6 L 8.59 7.41 L 13.17 12 l -4.58 4.59 L 10 18 l 6 -6 Z" />
                    </Icon>
                </ButtonLabel>
            </PrevButton>
            <ListWrap>
                <ContentsWrap translate={translate}>
                    {/* <PlayListCard />
                    <PlayListCard />
                    <PlayListCard />
                    <PlayListCard />
                    <PlayListCard />
                    <PlayListCard />
                    <PlayListCard />
                    <PlayListCard />
                    <PlayListCard /> */}
                    {staffPick.map((item, i) => {
                        return (
                            <CardContent onClick={() => selectPlay()}>
                                <ImgBox>
                                    <FeaturedLabel src="data:image/png;base64," alt="featuredImg" />
                                    <LeftImg src={item.image_url[0]} />
                                    <RightImg>
                                        <TopImg src={item.image_url[1]} />
                                        <BottomImg src={item.image_url[2]} />
                                    </RightImg>
                                </ImgBox>
                                <TextContent>
                                    <AvatarImgLink href="/insighttimer">
                                        <AvatarImg src="https://users.insighttimer.com/m7K4F2Q5S7X3a1j6U8a6X8B0a1e7R1g8E3z4b5k1C8L0Q8B3J6J0k8X5x2J1V6n2u9j3P1u6W0c3L2v3c8f3W7z6H7t4E1t6t0B0%2Fpictures%2Fsquare_small.jpeg?alt=media" alt="AvatarImg" />
                                    </AvatarImgLink>
                                    <TitleArea1>
                                        <TitleLink href="/meditation-playlists/khiMbkvXGuXQVtMTJjCq">
                                            <Title>{item.title}</Title>
                                        </TitleLink>
                                        <AvatarImgLink href="/insighttimer">
                                            <TitleDes>{item.teacher}</TitleDes>
                                        </AvatarImgLink>
                                    </TitleArea1>
                                </TextContent>
                                <DescriptionArea>
                                    <Description>
                                        {item.discribe}
                                    </Description>
                                </DescriptionArea>
                            </CardContent>
                        )
                    })}
                </ContentsWrap>
            </ListWrap>
            <AllplaylistsTitle>AllplaylistsTitle</AllplaylistsTitle>
            <SubTitle>Displaying 10856 playlists</SubTitle>
            <AllplaylistsWrap>
                <PlayWrapCard />
                <PlayWrapCard />
                <PlayWrapCard />
                <PlayWrapCard />
                <PlayWrapCard />
                <PlayWrapCard />
                <PlayWrapCard />
                <PlayWrapCard />
            </AllplaylistsWrap>
        </>
    )
}

export default PlayListPage;

